package day06;

public class Assing_operator {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 3;
		int b = 5;
		
		b=a;
		System.out.println(b);
		
		a+=1; // a=a+1
		System.out.println(a); // a=4
		
		a/=2; // a=a/2
		System.out.println(a); // a=2

		a*=2; // a=a*2
		System.out.println(a); // a=4
	}
}
