package day06;

public class ComplexOpx {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10;
		x = x + 10; 
		System.out.println(x);
		// 두번째 x에 저장된 값 10이 들어가고, + 10을 한 값 20이 x 변수에 다시 들어간다.
		// x = 20
		
		int y= 10;
		y+=10; // y = y + 10;
		System.out.println(y);
	}
}
